    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double a,b,power;
        
        try {
            a = Double.parseDouble(jTextField1.getText());
            b = Double.parseDouble(jTextField2.getText());
            power = Math.pow(a, b);
            JOptionPane.showMessageDialog(this, "Резлутат на степен: " + (power),"Резултат",1);
            
        } catch (Exception e) {
            jTextField1.setBackground(Color.red);
            jTextField2.setBackground(Color.red);
            JOptionPane.showMessageDialog(this, "Грешни данни", "Грешка",0);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        int otg = JOptionPane.showConfirmDialog(this, "Потвърждавате ли?", "Потвърждение", JOptionPane.YES_NO_OPTION);
        String a = jTextField1.getText();
        if (otg == JOptionPane.YES_OPTION) {
            list.addElement(a);
            jComboBox1.addItem(a);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

