    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        String otg = JOptionPane.showInputDialog("Въведи:");
        list.addElement(otg);
        jComboBox1.addItem(otg);
        
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        String text = jTextField2.getText();
        String cbox = jComboBox1.getSelectedItem().toString();
        
        String concat = text + " " + cbox;
        jButton4.setText(concat);
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        int a,b;
        try {
            a = Integer.parseInt(jTextField1.getText());
            b = Integer.parseInt(jTextField2.getText());
            if (a % b == 0) {
                
                jButton5.setText("Yes");
            }
            else{
                jButton5.setText("No");
                list.addElement(String.valueOf(a/b));
            }
            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Грешни данни", "Грешка",0);
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        int n = Integer.parseInt(jTextField1.getText());
        for (int i = 0; i < n; i++) {
            JLabel label;
            label = new JLabel();
            jPanel1.add(label);
            label.setSize(200,20);
            label.setText("тест");
            label.setLocation(0,i*20);
            
        }
    }//GEN-LAST:event_jButton6ActionPerformed

